package com.rock.slidingdraw.widget;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;import android.widget.RelativeLayout;/** * user can set current relativelayout whether has intercepte function * @author rock */public class IntercepteRelativeLayout extends RelativeLayout {	private boolean intercepte;	public IntercepteRelativeLayout(Context context) {		super(context);	}	public IntercepteRelativeLayout(Context context, AttributeSet attrs) {		super(context, attrs);	}	public IntercepteRelativeLayout(Context context, AttributeSet attrs, int defStyle) {		super(context, attrs, defStyle);	}	@Override	public boolean onInterceptTouchEvent(MotionEvent ev) {		return intercepte;	}	public boolean isIntercepte() {		return intercepte;	}	public void setIntercepte(boolean intercepte) {		this.intercepte = intercepte;	}}